import classes from './Modal.module.css';
import React, {Fragment} from 'react';
import ReactDOM from 'react-dom';

const Backdrop = props => (<div className={classes.backdrop}></div>);

const ModalOverlay = props => (<div className={classes.modal}>
  <div className={classes.content}>{props.children}</div>
</div>);

const portalElement = document.getElementById('overlays');

const Modal = props => (<Fragment>
  {ReactDOM.createPortal(<Backdrop/>)}
  {ReactDOM.createPortal(<ModalOverlay>{props.children}</ModalOverlay>)}</Fragment>);

  export default Modal;
